    private void btnLuuActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLuuActionPerformed
        // TODO add your handling code here:
        if (them) {
            if (KTDL()) {
                String thang = cboThang.getSelectedItem().toString();
                try {
                    Connection conn = dao.getConnect();
                    String caulenh = "INSERT INTO tbl_kyluong VALUES(null,'" + thang + "','" + txtTienLuong.getText() + "')";
                    boolean a = dao.Lenh(caulenh, conn);
                    if (a) {
                        tblKyLuong.removeAll();
                        tbl.setRowCount(0);
                        loadNV();
                        JOptionPane.showMessageDialog(this, "Thêm thành công");
                    } else {
                        JOptionPane.showMessageDialog(this, "Thêm KHÔNG thành công");
                    }
                } catch (SQLException ex) {
                    Logger.getLogger(frmKyLuong.class.getName()).log(Level.SEVERE, null, ex);
                }
            }
        }
        if (sua) {
            Integer makl = Integer.parseInt(txtMaKyLuong.getText());
            String thang = cboThang.getSelectedItem().toString();
            try {
                Connection conn = dao.getConnect();
                String caulenh = "UPDATE tbl_kyluong SET Thang='" + thang + "',TienLuong='" + txtTienLuong.getText() + "'where MaKL=" + makl;
                boolean a = dao.Lenh(caulenh, conn);
                if (a) {
                    tblKyLuong.removeAll();
                    tbl.setRowCount(0);
                    loadNV();
                    JOptionPane.showMessageDialog(this, "Sửa thành công");
                } else {
                    JOptionPane.showMessageDialog(this, "Sửa KHÔNG thành công");
                }
            } catch (SQLException ex) {
                Logger.getLogger(frmKyLuong.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
        if (xoa) {
            Integer makl = Integer.parseInt(txtMaKyLuong.getText());

            try {
                Connection conn = dao.getConnect();
                String caulenh = "DELETE FROM tbl_kyluong WHERE MaKL=" + makl;
                boolean a = dao.Lenh(caulenh, conn);
                if (a) {
                    tblKyLuong.removeAll();
                    tbl.setRowCount(0);
                    loadNV();
                    JOptionPane.showMessageDialog(this, "Xóa thành công");
                } else {
                    JOptionPane.showMessageDialog(this, "Xóa KHÔNG thành công");
                }
            } catch (SQLException ex) {
                Logger.getLogger(frmKyLuong.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }//GEN-LAST:event_btnLuuActionPerformed
    void hienthitxt(int i) {
        TableModel model = tblKyLuong.getModel();
        txtMaKyLuong.setText(model.getValueAt(i, 0).toString());
        cboThang.setSelectedItem(model.getValueAt(i, 1).toString());
        txtTienLuong.setText(model.getValueAt(i, 2).toString());

    }
    private void btnHuyActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnHuyActionPerformed
        // TODO add your handling code here:
        txtMaKyLuong.setText("");
        txtTienLuong.setText("");
        tblKyLuong.removeAll();
        tbl.setRowCount(0);
        loadNV();
        btnThem.setEnabled(true);
        btnThem.setBackground(mauxanh);
        btnSua.setEnabled(true);
        btnSua.setBackground(mauxanh);
        btnXoa.setEnabled(true);
        btnXoa.setBackground(mauxanh);
        btnLuu.setEnabled(false);
        btnLuu.setBackground(Color.gray);
    }//GEN-LAST:event_btnHuyActionPerformed

    private void tblKyLuongMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tblKyLuongMouseClicked
        // TODO add your handling code here:
        int i = tblKyLuong.getSelectedRow();
        hienthitxt(i);
    }//GEN-LAST:event_tblKyLuongMouseClicked

    private void lblSearchMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_lblSearchMouseClicked
        // TODO add your handling code here:
        tblKyLuong.removeAll();
        tbl.setRowCount(0);
        String tk = txtTimKiem.getText();
        ArrayList<TblKyluong> list;
        try {
            list = klDL.getListKyLuong(tk);
            tbl = (DefaultTableModel) tblKyLuong.getModel();
            for (TblKyluong nv : list) {
                tbl.addRow(new Object[]{nv.getMaKL(), nv.getThang(), nv.getTienLuong()});
            }
        } catch (SQLException ex) {

        }
    }//GEN-LAST:event_lblSearchMouseClicked


