    void loadAnh(JLabel lbl, String fileanh) {
        //TODO add your handling code here:
        ImageIcon icon = new ImageIcon(fileanh);
        Image image = icon.getImage().getScaledInstance(40, 40, Image.SCALE_SMOOTH);
        lbl.setIcon(new ImageIcon(image));
    }

    void loadNV() {
        try {
            ArrayList<TblPhongban> list;
            list = pbDL.getListNV();
            table = (DefaultTableModel) tblPhongBan.getModel();
            for (TblPhongban nv : list) {
                table.addRow(new Object[]{nv.getMaPB(), nv.getTenPB(), nv.getTruongPhong()});
            }
        } catch (SQLException ex) {
            Logger.getLogger(frmPhongBan.class.getName()).log(Level.SEVERE, null, ex);
        }
    }

    private boolean KTDL() throws HeadlessException {
        StringBuilder sb = new StringBuilder();
        if (txtTenPhongBan.getText().equals("")) {
            sb.append("Vui lòng nhập tên phòng ban\n");
            txtTenPhongBan.requestFocus();
        }
        if (txtTruongPhong.getText().equals("")) {
            sb.append("Vui lòng nhập trưởng phòng\n");
            txtTruongPhong.requestFocus();
        }
        if (sb.length() > 0) {
            JOptionPane.showMessageDialog(this, sb.toString(), "Thông báo", JOptionPane.ERROR_MESSAGE);
            return false;
        } else {
            return true;
        }
    }

