    private void btnLuuActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLuuActionPerformed
        // TODO add your handling code here:
        if (them) {
            if (KTDL()) {
                try {
                    Connection conn = dao.getConnect();
                    String caulenh = "INSERT INTO tbl_phongban VALUES(null,N'" + txtTenPhongBan.getText() + "',N'" + txtTruongPhong.getText() + "')";
                    boolean a = dao.Lenh(caulenh, conn);
                    if (a) {
                        tblPhongBan.removeAll();
                        table.setRowCount(0);
                        loadNV();
                        JOptionPane.showMessageDialog(this, "Thêm thành công");
                    } else {
                        JOptionPane.showMessageDialog(this, "Thêm KHÔNG thành công");
                    }
                } catch (SQLException ex) {
                    Logger.getLogger(frmPhongBan.class.getName()).log(Level.SEVERE, null, ex);
                }
            }
        }
        if (sua) {
            Integer mapb = Integer.parseInt(txtMaPhongBan.getText());
            try {
                Connection conn = dao.getConnect();
                String caulenh = "UPDATE tbl_phongban SET TenPB=N'" + txtTenPhongBan.getText() + "',TruongPhong=N'" + txtTruongPhong.getText() + "'where MaPB=" + mapb;
                boolean a = dao.Lenh(caulenh, conn);
                if (a) {
                    tblPhongBan.removeAll();
                    table.setRowCount(0);
                    loadNV();
                    JOptionPane.showMessageDialog(this, "Sửa thành công");
                } else {
                    JOptionPane.showMessageDialog(this, "Sửa KHÔNG thành công");
                }
            } catch (SQLException ex) {
                Logger.getLogger(frmPhongBan.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
        if (xoa) {
            Integer mapb = Integer.parseInt(txtMaPhongBan.getText());
            try {
                Connection conn = dao.getConnect();
                String caulenh = "DELETE FROM tbl_phongban WHERE MaPB=" + mapb;
                boolean a = dao.Lenh(caulenh, conn);
                if (a) {
                    tblPhongBan.removeAll();
                    table.setRowCount(0);
                    loadNV();
                    JOptionPane.showMessageDialog(this, "Xóa thành công");
                } else {
                    JOptionPane.showMessageDialog(this, "Xóa KHÔNG thành công");
                }
            } catch (SQLException ex) {
                Logger.getLogger(frmPhongBan.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }//GEN-LAST:event_btnLuuActionPerformed
    void hienthitxt(int i) {
        TableModel model = tblPhongBan.getModel();
        txtMaPhongBan.setText(model.getValueAt(i, 0).toString());
        txtTenPhongBan.setText(model.getValueAt(i, 1).toString());
        txtTruongPhong.setText(model.getValueAt(i, 2).toString());

    }
