    private void tblPhongBanMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tblPhongBanMouseClicked
        // TODO add your handling code here:
        int i = tblPhongBan.getSelectedRow();
        hienthitxt(i);
    }//GEN-LAST:event_tblPhongBanMouseClicked

    private void lblSearchMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_lblSearchMouseClicked
        // TODO add your handling code here:
        tblPhongBan.removeAll();
        table.setRowCount(0);

        String tk = txtTimKiem.getText();
        try {
            ArrayList<TblPhongban> list;
            list = pbDL.getListPhongBan(tk);
            table = (DefaultTableModel) tblPhongBan.getModel();
            for (TblPhongban nv : list) {
                table.addRow(new Object[]{nv.getMaPB(), nv.getTenPB(), nv.getTruongPhong()});
            }
        } catch (SQLException ex) {
            Logger.getLogger(frmPhongBan.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_lblSearchMouseClicked


